PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX term:<http://nextprot.org/rdf/terminology/> 
 SELECT * where { 
 ?a rdfs:subClassOf  term:TS-0095. 
}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX term:<http://nextprot.org/rdf/terminology/> 
 SELECT ?a ?o where { 
 ?a rdfs:subClassOf term:TS-0095. 
?a rdfs:label ?o.}

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX term:<http://nextprot.org/rdf/terminology/> 
 SELECT ?a ?o where { 
 ?a rdfs:subClassOf*  term:TS-0095. 
?a rdfs:label ?o.}
